package com.example.springvalidation.annotation;import com.example.springvalidation.validator.YearMonthValidator;import javax.validation.Constraint;import javax.validation.Payload;import java.lang.annotation.Retention;import java.lang.annotation.Target;import static java.lang.annotation.ElementType.*;import static java.lang.annotation.ElementType.TYPE_USE;import static java.lang.annotation.RetentionPolicy.RUNTIME;// YearMonthValidator 클래스를 가져와야 함.@Constraint(validatedBy = {YearMonthValidator.class})@Target({METHOD, FIELD, ANNOTATION_TYPE, CONSTRUCTOR, PARAMETER, TYPE_USE})@Retention(RUNTIME)public @interface YearMonth {    String message() default "yyyyMM 형식에 맞지 않습니다!";    Class<?>[] groups() default {};    Class<? extends Payload>[] payload() default {};    // pattern을 만들것. default를 줘서 꼭 안받아도 됨.    String pattern() default "yyyyMMdd";}