package com.example.springvalidation.validator;import com.example.springvalidation.annotation.YearMonth;import javax.validation.ConstraintValidator;import javax.validation.ConstraintValidatorContext;import java.time.LocalDate;import java.time.format.DateTimeFormatter;/** * @YearMonth에 대한 validator구현에 대한 부분. ConstraintValidator를 상속 받아 사용. */public class YearMonthValidator implements ConstraintValidator<YearMonth, String> {    private String pattern;    @Override    public void initialize(YearMonth constraintAnnotation) {        this.pattern = constraintAnnotation.pattern();    }    @Override    public boolean isValid(String value, ConstraintValidatorContext context) {        try {            LocalDate localDate = LocalDate.parse(value + "01",                    DateTimeFormatter.ofPattern(this.pattern));        } catch (Exception e) {            return false;        }        return true;    }}